import React, { useState } from 'react';

function App() {
  const [activeTab, setActiveTab] = useState('herbs');

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="App">
      <h1>Ayurvedic Information System</h1>
      <div className="tabs">
        <button
          className={activeTab === 'herbs' ? 'active' : ''}
          onClick={() => handleTabChange('herbs')}
        >
          Herbs
        </button>
        <button
          className={activeTab === 'aiDoctor' ? 'active' : ''}
          onClick={() => handleTabChange('aiDoctor')}
        >
          AI Doctor
        </button>
      </div>
      <div className="tab-content">
        {activeTab === 'herbs' && (
          <div>
            <h2>Herbs Tab</h2>
            {/* Add Herb related content here */}
          </div>
        )}
        {activeTab === 'aiDoctor' && (
          <div>
            <h2>AI Doctor Tab</h2>
            {/* Add AI Doctor related content here */}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
