import React from 'react';
import styles from './RemedyDetails.module.css'

const RemedyDetails = ({ remedyData }) => {

    const search = (data, key, value) => {
        const lcValue = value.toLowerCase();
        return data.filter(item => item[key].toLowerCase().includes(lcValue));
      };

      return (
        <div>
          <h1>{remedyData.Diagnosis}</h1>
          <h2>What to eat:</h2>
          <ul>
            {remedyData["What to eat"].map((item, index) => (
              <li key={index}>
                <strong>{item.Food}:</strong> {item.Reason}
              </li>
            ))}
          </ul>
          <h2>How to prepare it:</h2>
          <ul>
            {remedyData["How to prepare it"].map((item, index) => (
              <li key={index}>
                <strong>{item.Food}:</strong> {item.Preparation}
              </li>
            ))}
          </ul>
          <h2>When to eat:</h2>
          <ul>
            {remedyData["When to eat"].map((item, index) => (
              <li key={index}>
                <strong>{item.Time}:</strong> {item.Meal}
              </li>
            ))}
          </ul>
        </div>
      );

};

export default RemedyDetails;
